import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const ReviewPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { productId, imageUrl } = location.state || {};  // Make sure imageUrl is passed

  // State for review text, rating, etc.
  const [reviewText, setReviewText] = useState('');
  const [rating, setRating] = useState(0);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  const handleTextChange = (e) => setReviewText(e.target.value);

  const handleRatingChange = (newRating) => setRating(newRating);

  const handleSubmit = () => {
    if (reviewText.trim() && rating > 0) {
      setIsSubmitting(true);
      setTimeout(() => {
        setIsSubmitting(false);
        setShowSuccessMessage(true);
        setTimeout(() => {
          setShowSuccessMessage(false);
          setReviewText('');
          setRating(0);
        }, 2000);
      }, 1000);
    } else {
      alert('Please provide a review and rating.');
    }
  };

  const handleFAQClick = () => {
    navigate('/faqs');  // Navigate to the FAQ page
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        backgroundColor: '#f8f9fa',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '20px',
      }}
    >
      <div
        style={{
          width: '100%',
          maxWidth: '800px',
          backgroundColor: 'white',
          padding: '30px',
          borderRadius: '12px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          textAlign: 'center',
        }}
      >
        <h2 style={{ color: '#343a40' }}>Write a Review for Product {productId}</h2>

        {/* Display the product image */}
        {imageUrl && (
          <div style={{ marginBottom: '20px' }}>
            <img
              src={imageUrl}
              alt={`Product ${productId}`}
              style={{ width: '100%', maxWidth: '300px', borderRadius: '8px' }}
            />
          </div>
        )}

        <div style={{ marginBottom: '20px' }}>
          <p>Rating:</p>
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              onClick={() => handleRatingChange(star)}
              style={{
                fontSize: '2rem',
                cursor: 'pointer',
                color: star <= rating ? '#ffc107' : '#e4e5e9',
                margin: '0 5px',
              }}
            >
              â˜…
            </span>
          ))}
        </div>

        <textarea
          value={reviewText}
          onChange={handleTextChange}
          placeholder="Write your review here..."
          rows="6"
          style={{
            width: '100%',
            padding: '10px',
            marginBottom: '20px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            fontSize: '1rem',
          }}
        ></textarea>

        <button
          onClick={handleSubmit}
          disabled={isSubmitting}
          style={{
            backgroundColor: '#28a745',
            color: 'white',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '5px',
            fontSize: '1rem',
            cursor: 'pointer',
            width: '100%',
          }}
        >
          {isSubmitting ? 'Submitting...' : 'Submit Review'}
        </button>

        {showSuccessMessage && (
          <div
            style={{
              marginTop: '20px',
              backgroundColor: '#28a745',
              color: 'white',
              padding: '10px',
              borderRadius: '5px',
              fontSize: '1rem',
            }}
          >
            Review submitted successfully!
          </div>
        )}

        {/* FAQ Button */}
        <button
          onClick={handleFAQClick}
          style={{
            backgroundColor: '#007bff',
            color: 'white',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '5px',
            fontSize: '1rem',
            cursor: 'pointer',
            marginTop: '20px',
            width: '100%',
          }}
        >
          Go to FAQs
        </button>
      </div>
    </div>
  );
};

export default ReviewPage;
