import React, { useState } from "react";

const Review = ({ productId, onReviewSubmit }) => {
  // State for the review text, rating, and popup visibility
  const [reviewText, setReviewText] = useState("");
  const [rating, setRating] = useState(0);
  const [showForm, setShowForm] = useState(true); // State to toggle form visibility
  const [showSuccessMessage, setShowSuccessMessage] = useState(false); // Show success message
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Handle review text change
  const handleTextChange = (e) => {
    setReviewText(e.target.value);
  };

  // Handle rating change (Star click)
  const handleRatingChange = (newRating) => {
    setRating(newRating);
  };

  // Handle review submission
  const handleSubmit = () => {
    if (reviewText.trim() !== "" && rating > 0) {
      setIsSubmitting(true); // Start submission
      // Simulate an API call or your backend logic here
      setTimeout(() => {
        setIsSubmitting(false);
        setShowSuccessMessage(true); // Show success message after submission
        setReviewText(""); // Reset review text
        setRating(0); // Reset rating after submission
        setShowForm(false); // Hide the form after successful submission
        if (onReviewSubmit) {
          onReviewSubmit(); // Callback after review is submitted
        }
      }, 1500);
    } else {
      alert("Please write a review and give a rating before submitting.");
    }
  };

  // Inline styles for the component
  const styles = {
    reviewContainer: {
      backgroundColor: "white",
      padding: "20px",
      borderRadius: "10px",
      boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
      maxWidth: "500px",
      margin: "0 auto",
      textAlign: "center",
    },
    textarea: {
      width: "100%",
      padding: "10px",
      marginTop: "10px",
      borderRadius: "5px",
      border: "1px solid #ccc",
    },
    button: {
      padding: "10px 20px",
      marginTop: "10px",
      backgroundColor: "#4CAF50",
      color: "white",
      border: "none",
      borderRadius: "5px",
      cursor: "pointer",
    },
    buttonHover: {
      backgroundColor: "#45a049",
    },
    successMessage: {
      marginTop: "20px",
      padding: "10px",
      backgroundColor: "#28a745",
      color: "white",
      borderRadius: "5px",
    },
    star: {
      fontSize: "1.5rem",
      cursor: "pointer",
      color: "#ffc107",
    },
    starInactive: {
      color: "#e4e5e9",
    },
  };

  return (
    <div style={styles.reviewContainer}>
      {/* Success Message */}
      {showSuccessMessage && (
        <div style={styles.successMessage}>
          <p>Review added successfully!</p>
        </div>
      )}

      {/* Form to write a review */}
      {showForm ? (
        <>
          {/* Rating Section */}
          <div style={{ marginBottom: "10px" }}>
            <p>Rating:</p>
            {[1, 2, 3, 4, 5].map((star) => (
              <span
                key={star}
                onClick={() => handleRatingChange(star)}
                style={star <= rating ? styles.star : { ...styles.star, ...styles.starInactive }}
              >
                â˜…
              </span>
            ))}
          </div>

          {/* Review Textarea */}
          <textarea
            value={reviewText}
            onChange={handleTextChange}
            placeholder="Write your review here..."
            rows="4"
            cols="50"
            style={styles.textarea}
          ></textarea>
          <br />

          {/* Submit Button */}
          <button
            onClick={handleSubmit}
            style={styles.button}
            disabled={isSubmitting}
            onMouseOver={(e) => (e.target.style.backgroundColor = styles.buttonHover.backgroundColor)}
            onMouseOut={(e) => (e.target.style.backgroundColor = styles.button.backgroundColor)}
          >
            {isSubmitting ? "Submitting..." : "Submit Review"}
          </button>
        </>
      ) : (
        <p>Your review has been submitted successfully. Thank you!</p>
      )}
    </div>
  );
};

export default Review;
