import React, { useState, useEffect, useRef } from 'react';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { from: 'bot', text: 'üèí Welcome to Hofistis ‚Äî your home for top hockey stick brands!' }
  ]);
  const [input, setInput] = useState('');
  const messageEndRef = useRef(null);

  const toggleChatbot = () => setIsOpen(!isOpen);

  const scrollToBottom = () => {
    messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = () => {
    if (!input.trim()) return;
    const userMessage = { from: 'user', text: input };
    const botResponse = { from: 'bot', text: getBotResponse(input) };

    setMessages((prev) => [...prev, userMessage, botResponse]);
    setInput('');
  };

  const getBotResponse = (input) => {
    const msg = input.toLowerCase();

    if (msg.includes('hello') || msg.includes('hi')) return 'üëã Hi there! Looking for a specific hockey stick brand?';
    if (msg.includes('price') || msg.includes('cost')) return 'üí∞ Sticks range from Ksh 3,500 to Ksh 25,000 depending on the brand.';
    if (msg.includes('brand')) return 'üè∑Ô∏è We stock Bauer, CCM, Warrior, and True. Need help choosing one?';
    if (msg.includes('product') || msg.includes('stick')) return '‚úÖ We sell junior, intermediate, and pro-level hockey sticks.';
    if (msg.includes('delivery') || msg.includes('shipping')) return 'üöö We deliver across Kenya in 2‚Äì4 days. Free shipping above Ksh 5,000!';
    if (msg.includes('order')) return 'üì¶ You can track your order in your account. Need help placing one?';
    if (msg.includes('return') || msg.includes('refund')) return 'üîÅ Returns accepted within 14 days if unused. Email returns@hofistis.com.';
    
    return "ü§ñ I'm here to help with sticks, orders, or delivery! Ask me anything.";
  };

  return (
    <>
      {/* Floating Toggle Button */}
      <button
        onClick={toggleChatbot}
        style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          backgroundColor: '#1d3557',
          color: 'white',
          border: 'none',
          borderRadius: '50%',
          width: '56px',
          height: '56px',
          fontSize: '26px',
          cursor: 'pointer',
          zIndex: 1000,
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)',
        }}
      >
        üí¨
      </button>

      {/* Chatbox */}
      <div
        style={{
          position: 'fixed',
          bottom: isOpen ? '90px' : '-500px',
          right: '20px',
          width: '320px',
          maxHeight: '500px',
          backgroundColor: 'white',
          border: '1px solid #ccc',
          borderRadius: '12px',
          boxShadow: '0 0 12px rgba(0, 0, 0, 0.2)',
          display: 'flex',
          flexDirection: 'column',
          transition: 'bottom 0.4s ease',
          zIndex: 999,
          overflow: 'hidden',
        }}
      >
        {/* Header */}
        <div
          style={{
            backgroundColor: '#1d3557',
            color: 'white',
            padding: '12px 16px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            fontWeight: 'bold',
          }}
        >
          Hofistis Assistant
          <button
            onClick={toggleChatbot}
            style={{
              background: 'transparent',
              border: 'none',
              color: 'white',
              fontSize: '20px',
              cursor: 'pointer',
            }}
          >
            √ó
          </button>
        </div>

        {/* Messages */}
        <div
          style={{
            flex: 1,
            padding: '12px',
            overflowY: 'auto',
            background: '#f7f9fb',
          }}
        >
          {messages.map((msg, idx) => (
            <div
              key={idx}
              style={{
                backgroundColor: msg.from === 'bot' ? '#d0ebff' : '#b2f2bb',
                alignSelf: msg.from === 'bot' ? 'flex-start' : 'flex-end',
                marginBottom: '8px',
                padding: '10px 14px',
                borderRadius: '18px',
                maxWidth: '75%',
                fontSize: '0.95rem',
                textAlign: msg.from === 'bot' ? 'left' : 'right',
              }}
            >
              {msg.text}
            </div>
          ))}
          <div ref={messageEndRef} />
        </div>

        {/* Input */}
        <div
          style={{
            display: 'flex',
            padding: '10px',
            borderTop: '1px solid #ddd',
            backgroundColor: '#ffffff',
          }}
        >
          <input
            type="text"
            value={input}
            placeholder="Ask about sticks..."
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSend()}
            style={{
              flex: 1,
              padding: '8px 12px',
              borderRadius: '8px',
              border: '1px solid #ccc',
              fontSize: '0.9rem',
            }}
          />
          <button
            onClick={handleSend}
            style={{
              marginLeft: '8px',
              backgroundColor: '#1d3557',
              color: 'white',
              border: 'none',
              padding: '8px 14px',
              borderRadius: '8px',
              cursor: 'pointer',
            }}
          >
            Send
          </button>
        </div>
      </div>
    </>
  );
};

export default Chatbot;
