import React, { useLocation } from 'react';

const ReviewPage = () => {
  const location = useLocation();
  const { state } = location;

  // Access product details from state
  const { productId, productName, productDescription, productImage } = state;

  return (
    <div className="container py-4">
      <h1 className="text-center" style={{ color: '#28a745' }}>Product Review</h1>

      <div className="card" style={{ borderRadius: '12px', padding: '20px' }}>
        <div className="text-center">
          <img src={productImage} alt={productName} className="img-fluid" style={{ maxWidth: '300px' }} />
        </div>

        <h3 className="mt-3">{productName}</h3>
        <p>{productDescription}</p>

        <h5>Your Review:</h5>
        {/* Here you can add a review form or simply display submitted reviews */}
        <textarea
          placeholder="Write your review here..."
          className="form-control"
          rows="5"
        ></textarea>

        <button className="btn mt-3" style={{ backgroundColor: '#28a745', color: 'white' }}>
          Submit Review
        </button>
      </div>
    </div>
  );
};

export default ReviewPage;
