import React, { useState } from "react";

const Review = ({ productId, onReviewSubmit }) => {
  // State for the review text, rating, and popup visibility
  const [reviewText, setReviewText] = useState("");
  const [rating, setRating] = useState(0);
  const [showForm, setShowForm] = useState(true); // State to toggle form visibility
  const [showSuccessMessage, setShowSuccessMessage] = useState(false); // Show success message
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errorMessage, setErrorMessage] = useState(""); // To display error messages

  // Handle review text change
  const handleTextChange = (e) => {
    setReviewText(e.target.value);
    setErrorMessage(""); // Reset error message when user types
  };

  // Handle rating change (Star click)
  const handleRatingChange = (newRating) => {
    setRating(newRating);
  };

  // Handle review submission
  const handleSubmit = () => {
    if (reviewText.trim() === "" || rating === 0) {
      setErrorMessage("Please write a review and give a rating before submitting.");
      return;
    }

    setIsSubmitting(true); // Start submission
    // Simulate an API call or your backend logic here
    setTimeout(() => {
      setIsSubmitting(false);
      setShowSuccessMessage(true); // Show success message after submission
      setReviewText(""); // Reset review text
      setRating(0); // Reset rating after submission
      setShowForm(false); // Hide the form after successful submission
      if (onReviewSubmit) {
        onReviewSubmit(); // Callback after review is submitted
      }

      // Hide the success message after 3 seconds
      setTimeout(() => {
        setShowSuccessMessage(false);
      }, 3000);
    }, 1500);
  };

  // Inline styles for the component
  const styles = {
    container: {
      position: "relative",
      maxWidth: "600px", // Set a max width for the container
      margin: "0 auto",
      paddingTop: "40px",
    },
    reviewContainer: {
      backgroundColor: "white",
      padding: "20px",
      borderRadius: "15px",
      boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
      textAlign: "center",
      zIndex: 1000,
      position: "absolute",
      top: "50%",
      left: "50%",
      transform: "translate(-50%, -50%)",
      backgroundColor: "rgba(0, 0, 0, 0.7)", // Semi-transparent dark background
      color: "white", // Text color to ensure visibility on the dark background
      padding: "20px",
      width: "90%",
      maxWidth: "500px",
      borderRadius: "12px",
    },
    imageContainer: {
      width: "100%",
      height: "auto",
      position: "relative",
    },
    image: {
      width: "100%",
      height: "auto",
      borderRadius: "12px",
      display: "block",
    },
    textarea: {
      width: "100%",
      padding: "10px",
      marginTop: "10px",
      borderRadius: "5px",
      border: "1px solid #ccc",
      fontSize: "1rem",
    },
    button: {
      padding: "10px 20px",
      marginTop: "10px",
      backgroundColor: "#4CAF50",
      color: "white",
      border: "none",
      borderRadius: "5px",
      cursor: "pointer",
      transition: "background-color 0.3s ease",
      fontSize: "1rem",
    },
    buttonHover: {
      backgroundColor: "#45a049",
    },
    successMessage: {
      marginTop: "20px",
      padding: "10px",
      backgroundColor: "#28a745",
      color: "white",
      borderRadius: "5px",
      fontSize: "1rem",
    },
    errorMessage: {
      marginTop: "10px",
      padding: "10px",
      backgroundColor: "#dc3545",
      color: "white",
      borderRadius: "5px",
      fontSize: "1rem",
    },
    star: {
      fontSize: "1.5rem",
      cursor: "pointer",
      color: "#ffc107",
      margin: "0 5px",
    },
    starInactive: {
      color: "#e4e5e9",
    },
    spinner: {
      border: "4px solid #f3f3f3", /* Light grey */
      borderTop: "4px solid #3498db", /* Blue */
      borderRadius: "50%",
      width: "24px",
      height: "24px",
      animation: "spin 2s linear infinite",
    },
  };

  return (
    <div style={styles.container}>
      {/* Image */}
      <div style={styles.imageContainer}>
        <img
          src="https://via.placeholder.com/500x300" // Replace with your actual image URL
          alt="Product"
          style={styles.image}
        />

        {/* Review Form */}
        {showForm && (
          <div style={styles.reviewContainer}>
            {/* Rating Section */}
            <div style={{ marginBottom: "10px" }}>
              <p>Rating:</p>
              {[1, 2, 3, 4, 5].map((star) => (
                <span
                  key={star}
                  onClick={() => handleRatingChange(star)}
                  style={star <= rating ? styles.star : { ...styles.star, ...styles.starInactive }}
                >
                  â˜…
                </span>
              ))}
            </div>

            {/* Review Textarea */}
            <textarea
              value={reviewText}
              onChange={handleTextChange}
              placeholder="Write your review here..."
              rows="4"
              cols="50"
              style={styles.textarea}
            ></textarea>
            <br />

            {/* Submit Button */}
            <button
              onClick={handleSubmit}
              style={styles.button}
              disabled={isSubmitting}
              onMouseOver={(e) => (e.target.style.backgroundColor = styles.buttonHover.backgroundColor)}
              onMouseOut={(e) => (e.target.style.backgroundColor = styles.button.backgroundColor)}
            >
              {isSubmitting ? (
                <div style={styles.spinner}></div>
              ) : (
                "Submit Review"
              )}
            </button>

            {/* Error Message */}
            {errorMessage && <div style={styles.errorMessage}>{errorMessage}</div>}
          </div>
        )}

        {/* Success Message */}
        {showSuccessMessage && (
          <div style={styles.successMessage}>
            <p>Review added successfully!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Review;
