import React, { useState } from 'react';
import axios from 'axios';

const Review = ({ productId, onReviewSubmit }) => {
  const [rating, setRating] = useState(0);
  const [review, setReview] = useState('');
  const [message, setMessage] = useState('');

  const submitReview = async () => {
    if (rating === 0 || review === '') {
      setMessage('Please provide a rating and review.');
      return;
    }

    try {
      const response = await axios.post('https://mercymucee.pythonanywhere.com/api/add_review', {
        productId,
        rating,
        comment: review,
      });

      if (response.status === 201) {
        setMessage('Review added successfully!');
        onReviewSubmit(); // Close the modal or trigger an action after success.
      }
    } catch (error) {
      setMessage('Error submitting the review, please try again later.');
    }
  };

  return (
    <div className="review-modal">
      <div className="review-content">
        <h4>Write a Review</h4>
        <textarea 
          value={review} 
          onChange={(e) => setReview(e.target.value)} 
          placeholder="Write your review here..." 
        />

        <div className="rating-stars">
          {[1, 2, 3, 4, 5].map(star => (
            <span 
              key={star} 
              onClick={() => setRating(star)} 
              style={{ cursor: 'pointer', color: star <= rating ? '#ffc107' : '#e4e5e9' }}
            >
              â˜…
            </span>
          ))}
        </div>

        <button onClick={submitReview} className="btn btn-success">Submit Review</button>

        {message && <p>{message}</p>}
      </div>
    </div>
  );
};

export default Review;
