import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';  // Correct import

const ReviewPage = () => {
  const location = useLocation();
  const { state } = location || {};  // Destructure safely

  // Extract product details and review from state
  const { productId, productName, productDescription, productImage, reviewText } = state || {};

  const [review, setReview] = useState(reviewText || '');  // State to handle the review text
  const [submittedReview, setSubmittedReview] = useState('');  // To show the review after submission

  const handleReviewChange = (e) => {
    setReview(e.target.value);  // Update review text
  };

  const handleSubmitReview = () => {
    if (review.trim()) {
      // If review is not empty, submit the review and show it below
      setSubmittedReview(review);
      setReview('');  // Clear the review input after submission
    } else {
      alert('Please write a review before submitting!');
    }
  };

  return (
    <div className="container py-4">
      <h1 className="text-center" style={{ color: '#28a745' }}>Product Review</h1>

      <div className="card" style={{ borderRadius: '12px', padding: '20px' }}>
        <div className="text-center">
          <img
            src={productImage || 'default-image.jpg'}  // Use the image from the product or a default image if not available
            alt={productName || 'Product Image'}
            className="img-fluid"
            style={{ maxWidth: '300px' }}
          />
        </div>

        <h3 className="mt-3">{productName || 'Product Name'}</h3>
        <p>{productDescription || 'Product Description'}</p>

        {/* Review Form */}
        <h5>Your Review:</h5>
        <textarea
          value={review}
          onChange={handleReviewChange}
          placeholder="Write your review here..."
          className="form-control"
          rows="5"
          style={{ resize: 'none' }}
        ></textarea>

        <button
          className="btn mt-3"
          style={{ backgroundColor: '#28a745', color: 'white' }}
          onClick={handleSubmitReview}
        >
          Submit Review
        </button>

        {/* Display the submitted review below */}
        {submittedReview && (
          <div className="mt-4">
            <h5>Submitted Review:</h5>
            <p>{submittedReview}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ReviewPage;
