import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaDollarSign, FaShoppingCart } from "react-icons/fa";
import Chatbot from "./Chatbot"; // Assuming this is another component you have

const GetProducts = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [products, setProducts] = useState([]);
  const [reviewProductId, setReviewProductId] = useState(null); // Track which product is being reviewed
  const [productMessage, setProductMessage] = useState({});
  const navigate = useNavigate();

  // Fetch products from the API
  const getProducts = async () => {
    try {
      const response = await fetch("https://api.example.com/products"); // Replace with your actual API endpoint
      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }
      const data = await response.json();
      setProducts(data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    getProducts();
  }, []);

  const img_url = "https://yourapi.com/images/"; // Adjust this for your image URL base

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-KE", {
      style: "currency",
      currency: "KES",
    }).format(price);
  };

  const handleAddToCart = (product) => {
    setProductMessage((prev) => ({
      ...prev,
      [product.id]: "Adding to cart...",
    }));

    setTimeout(() => {
      // Add to cart logic here (e.g., context API or Redux)
      setProductMessage((prev) => ({
        ...prev,
        [product.id]: "Product added to cart successfully!",
      }));
      setTimeout(() => {
        setProductMessage((prev) => ({
          ...prev,
          [product.id]: "",
        }));
      }, 2000);
    }, 1000);
  };

  const handleReviewClick = (productId) => {
    setReviewProductId(productId); // Set the product ID for the review form
    navigate(`/review/${productId}`); // Navigate to the review page for this product
  };

  if (loading) return <p>Loading products...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="container py-4">
      <h1 className="mb-4 text-center" style={{ color: "#28a745" }}>
        Available Products
      </h1>

      <div className="row">
        {products.map((product) => (
          <div className="col-md-4 col-lg-3 mb-4" key={product.id}>
            <div
              className="card shadow-sm h-100 position-relative"
              style={{
                borderRadius: "12px",
                overflow: "hidden",
                backgroundColor: "#f8f9fa",
              }}
            >
              {/* Discount Badge */}
              <span
                className="badge position-absolute"
                style={{
                  top: "10px",
                  left: "10px",
                  backgroundColor: "#e63946",
                  color: "#fff",
                  borderRadius: "12px",
                  fontSize: "0.75rem",
                  padding: "6px 10px",
                }}
              >
                5% OFF
              </span>

              {/* Rating Badge */}
              <span
                className="badge position-absolute"
                style={{
                  top: "10px",
                  right: "10px",
                  backgroundColor: "#ffc107",
                  color: "#212529",
                  fontSize: "0.75rem",
                  padding: "6px 10px",
                  borderRadius: "12px",
                }}
              >
                ⭐ {product.avgRating || 0}
              </span>

              <img
                src={img_url + product.product_photo}
                alt={product.product_name}
                className="card-img-top"
                style={{ height: "220px", objectFit: "cover" }}
              />

              <div className="card-body d-flex flex-column">
                <h5 style={{ color: "#212529" }}>
                  {product.product_name}
                  <span
                    className="badge ms-2"
                    style={{
                      backgroundColor: "#17a2b8",
                      color: "#fff",
                      fontSize: "0.7rem",
                    }}
                  >
                    {product.reviews_count || 0} Reviews
                  </span>
                </h5>
                <p style={{ color: "#6c757d", fontSize: "0.9rem" }}>
                  {product.product_description}
                </p>

                <b style={{ color: "#ffc107" }}>
                  {formatPrice(product.product_cost)}
                </b>

                <button
                  className="btn mt-3 text-white"
                  style={{ backgroundColor: "#28a745" }}
                  onClick={() => navigate("/mpesapayment", { state: { product } })}
                >
                  <FaDollarSign className="me-2" /> Purchase Now
                </button>

                <button
                  className="btn mt-2 text-white"
                  style={{ backgroundColor: "#17a2b8" }}
                  onClick={() => handleAddToCart(product)}
                >
                  <FaShoppingCart className="me-2" /> Add to Cart
                </button>

                {/* Review Badge */}
                <span
                  className="badge position-absolute"
                  style={{
                    bottom: "10px",
                    left: "10px",
                    backgroundColor: "#ffc107",
                    color: "#212529",
                    fontSize: "0.75rem",
                    padding: "6px 10px",
                    borderRadius: "12px",
                    cursor: "pointer",
                  }}
                  onClick={() => handleReviewClick(product.id)}
                >
                  Write a Review
                </span>

                {productMessage[product.id] && (
                  <p className="mt-2" style={{ fontSize: "0.85rem" }}>
                    {productMessage[product.id]}
                  </p>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Floating Chatbot */}
      <div
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          zIndex: 1000,
        }}
      >
        <Chatbot />
      </div>
    </div>
  );
};

export default GetProducts;
