import React from 'react';
import { useLocation } from 'react-router-dom';

const ReviewPage = () => {
  // Use useLocation to retrieve the state passed during navigation
  const location = useLocation();
  const { state } = location || {};  // Destructure state safely

  // Destructure product data from state
  const { productId, productName, productDescription, productImage, reviewText } = state || {};

  return (
    <div className="container py-4">
      <h1 className="text-center" style={{ color: '#28a745' }}>Product Review</h1>

      <div className="card" style={{ borderRadius: '12px', padding: '20px' }}>
        <div className="text-center">
          <img
            src={productImage || ''}
            alt={productName || 'Product Image'}
            className="img-fluid"
            style={{ maxWidth: '300px' }}
          />
        </div>

        <h3 className="mt-3">{productName || 'Product Name'}</h3>
        <p>{productDescription || 'Product Description'}</p>

        <h5>Your Review:</h5>
        <p>{reviewText || 'No review submitted yet.'}</p>
      </div>
    </div>
  );
};

export default ReviewPage;
