import React, { useState, useEffect } from 'react';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);  // State for toggling chatbot visibility
  const [messages, setMessages] = useState([
    { from: 'bot', text: '🏒 Welcome to Hofistis — your home for top hockey stick brands!' }
  ]);
  const [input, setInput] = useState('');
  const [recognition, setRecognition] = useState(null); // For voice-to-text

  // Function to toggle chatbot visibility
  const toggleChatbot = () => setIsOpen(!isOpen);

  // Function to speak messages (Text-to-speech)
  const speakMessage = (message) => {
    if ('speechSynthesis' in window) {
      const msg = new SpeechSynthesisUtterance(message);
      msg.lang = 'en-US';
      window.speechSynthesis.speak(msg);
    } else {
      console.warn('Speech synthesis not supported in your browser.');
    }
  };

  // Function to handle sending a message
  const handleSend = () => {
    if (!input.trim()) return;  // Prevent sending empty messages

    const userMessage = { from: 'user', text: input };
    const botResponse = { from: 'bot', text: getBotResponse(input) };

    setMessages(prev => [...prev, userMessage, botResponse]);  // Update the message list
    speakMessage(botResponse.text);  // Speak the bot's response
    setInput('');  // Clear input field after sending
  };

  // Function to simulate bot responses
  const getBotResponse = (input) => {
    const msg = input.toLowerCase();

    if (msg.includes('hello') || msg.includes('hi')) return 'Hi there! 👋 Looking for a specific brand or type of hockey stick?';
    if (msg.includes('price') || msg.includes('cost')) return 'Our sticks range from Ksh 3,500 to Ksh 25,000 depending on the brand.';
    if (msg.includes('brand')) return 'We stock Bauer, CCM, Warrior, and True. Need help choosing one?';
    if (msg.includes('product') || msg.includes('stick')) return 'We sell junior, intermediate, and pro-level hockey sticks.';
    if (msg.includes('delivery') || msg.includes('shipping')) return 'We deliver across Kenya in 2–4 days. Free shipping above Ksh 5,000!';
    if (msg.includes('order')) return 'You can track your order in your account. Need help placing one?';
    if (msg.includes('return') || msg.includes('refund')) return 'Returns are accepted within 14 days if unused. Email returns@hofistis.com.';

    return "I'm here to help with hockey sticks, orders, or delivery! Ask me anything.";
  };

  // Initialize Speech Recognition (voice-to-text)
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognitionInstance = new SpeechRecognition();
      recognitionInstance.lang = 'en-US';
      recognitionInstance.continuous = false;
      recognitionInstance.interimResults = false;
      setRecognition(recognitionInstance);
    } else {
      console.warn('Speech Recognition not supported in this browser.');
    }
  }, []);

  // Function to start voice-to-text input
  const startVoiceRecognition = () => {
    if (recognition) {
      recognition.start();

      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setInput(transcript);
        handleSend(); // Send the message automatically after speech is recognized
      };

      recognition.onerror = (event) => {
        console.error('Speech recognition error', event.error);
      };
    }
  };

  return (
    <>
      {/* Toggle Chatbot Button */}
      <button
        style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          backgroundColor: '#1d3557',
          color: 'white',
          border: 'none',
          borderRadius: '50%',
          width: '55px',
          height: '55px',
          fontSize: '24px',
          cursor: 'pointer',
          zIndex: 1000,
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)'
        }}
        onClick={toggleChatbot}
      >
        💬
      </button>

      {/* Chatbot Window */}
      {isOpen && (
        <div
          style={{
            width: '300px',
            position: 'fixed',
            bottom: '90px',
            right: '20px',
            zIndex: 1000,
            background: 'white',
            borderRadius: '10px',
            border: '1px solid #ccc',
            boxShadow: '0 0 12px rgba(0, 0, 0, 0.1)',
            display: 'flex',
            flexDirection: 'column'
          }}
        >
          {/* Header with Close Button */}
          <div
            style={{
              backgroundColor: '#1d3557',
              color: 'white',
              padding: '10px',
              fontWeight: 'bold',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              borderTopLeftRadius: '10px',
              borderTopRightRadius: '10px'
            }}
          >
            Hofistis Assistant
            <button
              onClick={toggleChatbot}
              style={{
                background: 'transparent',
                border: 'none',
                color: 'white',
                fontSize: '20px',
                cursor: 'pointer'
              }}
            >
              ×
            </button>
          </div>

          {/* Chat Messages */}
          <div
            style={{
              height: '200px',
              overflowY: 'auto',
              padding: '10px',
              background: '#f9f9f9',
              flex: 1
            }}
          >
            {messages.map((msg, idx) => (
              <div
                key={idx}
                style={{
                  marginBottom: '10px',
                  padding: '8px 12px',
                  borderRadius: '15px',
                  maxWidth: '80%',
                  backgroundColor: msg.from === 'bot' ? '#e1f5fe' : '#bbdefb',
                  textAlign: msg.from === 'bot' ? 'left' : 'right',
                  marginLeft: msg.from === 'bot' ? 0 : 'auto'
                }}
              >
                {msg.text}
              </div>
            ))}
          </div>

          {/* Input Field */}
          <div
            style={{
              display: 'flex',
              padding: '10px',
              borderTop: '1px solid #ccc'
            }}
          >
            <input
              type="text"
              placeholder="Ask about hockey sticks..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSend()}
              style={{
                flex: 1,
                padding: '6px 10px',
                borderRadius: '4px',
                border: '1px solid #ccc'
              }}
            />
            <button
              onClick={handleSend}
              style={{
                marginLeft: '8px',
                padding: '6px 10px',
                backgroundColor: '#1d3557',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Send
            </button>

            {/* Voice Recognition Button */}
            <button
              onClick={startVoiceRecognition}
              style={{
                marginLeft: '8px',
                padding: '6px 10px',
                backgroundColor: '#17a2b8',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              🎤
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
