import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { CartContext } from './CartContext';
import { FaDollarSign, FaShoppingCart } from 'react-icons/fa';
import Review from './Review';

const Getproducts = () => {
  const [loading, setLoading] = useState('');
  const [error, setError] = useState('');
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedProductId, setSelectedProductId] = useState(null); // Track selected product for review form
  const [productMessage, setProductMessage] = useState({}); // To store the success message for each product
  const { addToCart } = useContext(CartContext);
  const navigate = useNavigate();

  // Fetch products from the API
  const get_products = async () => {
    setLoading('Please wait as we load the products...');
    try {
      const response = await axios.get('https://mercymucee.pythonanywhere.com/api/get_products_details');
      setProducts(response.data);
      setLoading('');
    } catch (error) {
      setLoading('');
      setError('Failed to load products. Please try again later.');
      console.error(error);
    }
  };

  useEffect(() => {
    get_products();
  }, []);

  useEffect(() => {
    const filtered = products.filter((product) =>
      product.product_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.product_description.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredProducts(filtered);
  }, [searchQuery, products]);

  const img_url = 'https://mercymucee.pythonanywhere.com/static/images/';

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-KE', {
      style: 'currency',
      currency: 'KES',
    }).format(price);
  };

  const handleAddToCart = (product) => {
    setProductMessage((prev) => ({
      ...prev,
      [product.id]: 'Adding to cart...',
    }));

    setTimeout(() => {
      addToCart(product);
      setProductMessage((prev) => ({
        ...prev,
        [product.id]: 'Product added to cart successfully!',
      }));
      setTimeout(() => {
        setProductMessage((prev) => ({
          ...prev,
          [product.id]: '',
        }));
      }, 2000);
    }, 1000);
  };

  const handleReviewClick = (productId) => {
    setSelectedProductId(productId);  // Show the review form for the clicked product
  };

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 0; i < 5; i++) {
      stars.push(
        <span key={i} style={{ color: i < rating ? '#ffc107' : '#e4e5e9' }}>★</span>
      );
    }
    return stars;
  };

  return (
    <div className="container py-4">
      <h1 className="mb-4 text-center" style={{ color: '#28a745' }}>Available Products</h1>

      <input
        type="text"
        className="form-control shadow-sm mb-4 p-2"
        placeholder="Search products..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {loading && <p>{loading}</p>}
      {error && <p style={{ color: '#e63946' }}>{error}</p>}

      <div className="row">
        {filteredProducts.map((product) => (
          <div className="col-md-4 col-lg-3 mb-4" key={product.id}>
            <div
              className="card shadow-sm h-100 position-relative"
              style={{
                borderRadius: '12px',
                overflow: 'hidden',
                backgroundColor: '#f8f9fa',
              }}
            >
              {/* Discount badge */}
              <span
                className="badge position-absolute"
                style={{
                  top: '10px',
                  left: '10px',
                  backgroundColor: '#e63946',
                  color: '#fff',
                  borderRadius: '12px',
                  fontSize: '0.75rem',
                  padding: '6px 10px',
                }}
              >
                5% OFF
              </span>

              {/* Rating badge */}
              <span
                className="badge position-absolute"
                style={{
                  top: '10px',
                  right: '10px',
                  backgroundColor: '#ffc107',
                  color: '#212529',
                  fontSize: '0.75rem',
                  padding: '6px 10px',
                  borderRadius: '12px',
                }}
              >
                ⭐ {product.avgRating || 0}
              </span>

              <img
                src={img_url + product.product_photo}
                alt={product.product_name}
                className="card-img-top"
                style={{ height: '220px', objectFit: 'cover' }}
              />

              <div className="card-body d-flex flex-column">
                <h5 style={{ color: '#212529' }}>
                  {product.product_name}
                  <span
                    className="badge ms-2"
                    style={{
                      backgroundColor: '#17a2b8',
                      color: '#fff',
                      fontSize: '0.7rem',
                    }}
                  >
                    {product.reviews_count || 0} Reviews
                  </span>
                </h5>
                <p style={{ color: '#6c757d', fontSize: '0.9rem' }}>
                  {product.product_description}
                </p>

                <b style={{ color: '#ffc107' }}>{formatPrice(product.product_cost)}</b>

                <button
                  className="btn mt-3 text-white"
                  style={{ backgroundColor: '#28a745' }}
                  onClick={() => navigate('/mpesapayment', { state: { product } })}
                >
                  <FaDollarSign className="me-2" /> Purchase Now
                </button>

                <button
                  className="btn mt-2 text-white"
                  style={{ backgroundColor: '#17a2b8' }}
                  onClick={() => handleAddToCart(product)}
                >
                  <FaShoppingCart className="me-2" /> Add to Cart
                </button>

                {/* Show success message for this product only */}
                {productMessage[product.id] && (
                  <p className="mt-2" style={{ fontSize: '0.85rem' }}>
                    {productMessage[product.id]}
                  </p>
                )}

                {/* Review Button as Badge */}
                <span
                  onClick={() => handleReviewClick(product.id)}
                  className="badge mt-2"
                  style={{
                    cursor: 'pointer',
                    backgroundColor: '#ffc107',
                    color: '#212529',
                    fontSize: '0.85rem',
                    padding: '8px 12px',
                    borderRadius: '12px',
                  }}
                >
                  Write a Review
                </span>

                {/* Show Review Form if this Product is Selected */}
                {selectedProductId === product.id && (
                  <div style={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    width: '80%',
                    backgroundColor: 'rgba(255, 255, 255, 0.9)', // Slightly transparent white
                    padding: '10px',
                    borderRadius: '8px',
                    boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
                    fontSize: '0.85rem',
                    zIndex: '1000',
                    maxWidth: '400px',
                    textAlign: 'center'
                  }}>
                    <Review productId={product.id} />
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="text-center mt-4">
        <Link to="/faqs" className="btn btn-link" style={{ color: '#28a745' }}>
          Go to FAQ
        </Link>
      </div>
    </div>
  );
};

export default Getproducts;
