import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { CartContext } from './CartContext';
import { FaDollarSign, FaShoppingCart } from 'react-icons/fa';
import Chatbot from './Chatbot';

const Getproducts = () => {
  const [loading, setLoading] = useState('');
  const [error, setError] = useState('');
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [productMessage, setProductMessage] = useState({});
  const [reviews, setReviews] = useState({});  // Store reviews for each product separately
  const [newReview, setNewReview] = useState('');
  const [rating, setRating] = useState(0);
  const [reviewMessage, setReviewMessage] = useState('');
  const [currentProductId, setCurrentProductId] = useState(null);  // Track the currently clicked product for reviews

  const { addToCart } = useContext(CartContext);
  const navigate = useNavigate();

  const get_products = async () => {
    setLoading('Please wait as we load the products...');
    try {
      const response = await axios.get('https://mercymucee.pythonanywhere.com/api/get_products_details');
      setProducts(response.data);
      setLoading('');
    } catch (error) {
      setLoading('');
      setError(error.message);
    }
  };

  const get_reviews = async (productId) => {
    try {
      const response = await axios.get(`https://mercymucee.pythonanywhere.com/api/get_reviews/${productId}`);
      setReviews((prev) => ({
        ...prev,
        [productId]: response.data,
      }));
    } catch (error) {
      console.error('Error fetching reviews:', error);
    }
  };

  const submitReview = async (productId) => {
    if (!newReview || rating === 0) {
      setReviewMessage('Please provide a rating and a review before submitting.');
      return;
    }

    try {
      // Submit review to backend
      const response = await axios.post('https://mercymucee.pythonanywhere.com/api/add_review', {
        productId,
        rating,
        comment: newReview,
      });

      if (response.status === 201) {
        setReviewMessage('Your review has been submitted successfully!');
        setNewReview('');
        setRating(0);
        get_reviews(productId);  // Refresh reviews after submitting
      }
    } catch (error) {
      setReviewMessage('There was an error submitting your review. Please try again later.');
    }
  };

  useEffect(() => {
    get_products();
  }, []);

  useEffect(() => {
    const filtered = products.filter((product) =>
      product.product_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.product_description.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredProducts(filtered);
  }, [searchQuery, products]);

  useEffect(() => {
    if (currentProductId) {
      get_reviews(currentProductId);  // Fetch reviews when a product is clicked
    }
  }, [currentProductId]);

  const img_url = 'https://mercymucee.pythonanywhere.com/static/images/';

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-KE', {
      style: 'currency',
      currency: 'KES',
    }).format(price);
  };

  const handleAddToCart = (product) => {
    setProductMessage((prev) => ({
      ...prev,
      [product.id]: 'Adding to cart...',
    }));

    setTimeout(() => {
      addToCart(product);
      setProductMessage((prev) => ({
        ...prev,
        [product.id]: 'Product added to cart successfully!',
      }));
      setTimeout(() => {
        setProductMessage((prev) => ({
          ...prev,
          [product.id]: '',
        }));
      }, 2000);
    }, 1000);
  };

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 0; i < 5; i++) {
      stars.push(
        <span key={i} style={{ color: i < rating ? '#ffc107' : '#e4e5e9' }}>★</span>
      );
    }
    return stars;
  };

  const calculateAverageRating = (reviewList) => {
    if (!reviewList || reviewList.length === 0) return 0;
    const total = reviewList.reduce((sum, r) => sum + r.rating, 0);
    return (total / reviewList.length).toFixed(1);
  };

  return (
    <div className="container py-4">
      <h1 className="mb-4 text-center" style={{ color: '#28a745' }}>Available Products</h1>

      <input
        type="text"
        className="form-control shadow-sm mb-4 p-2"
        placeholder="Search products..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {loading && <p>{loading}</p>}
      {error && <p style={{ color: '#e63946' }}>{error}</p>}

      <div className="row">
        {filteredProducts.map((product) => {
          const productReviews = reviews[product.id] || [];
          const avgRating = calculateAverageRating(productReviews);
          return (
            <div className="col-md-4 col-lg-3 mb-4" key={product.id}>
              <div
                className="card shadow-sm h-100 position-relative"
                style={{
                  borderRadius: '12px',
                  overflow: 'hidden',
                  backgroundColor: '#f8f9fa',
                }}
              >
                {/* Discount badge */}
                <span
                  className="badge position-absolute"
                  style={{
                    top: '10px',
                    left: '10px',
                    backgroundColor: '#e63946',
                    color: '#fff',
                    borderRadius: '12px',
                    fontSize: '0.75rem',
                    padding: '6px 10px',
                  }}
                >
                  5% OFF
                </span>

                {/* Rating badge */}
                <span
                  className="badge position-absolute"
                  style={{
                    top: '10px',
                    right: '10px',
                    backgroundColor: '#ffc107',
                    color: '#212529',
                    fontSize: '0.75rem',
                    padding: '6px 10px',
                    borderRadius: '12px',
                  }}
                >
                  ⭐ {avgRating}
                </span>

                <img
                  src={img_url + product.product_photo}
                  alt={product.product_name}
                  className="card-img-top"
                  style={{ height: '220px', objectFit: 'cover' }}
                  onClick={() => setCurrentProductId(product.id)}  // Set the current product for reviews
                />

                <div className="card-body d-flex flex-column">
                  <h5 style={{ color: '#212529' }}>
                    {product.product_name}
                    <span
                      className="badge ms-2"
                      style={{
                        backgroundColor: '#17a2b8',
                        color: '#fff',
                        fontSize: '0.7rem',
                      }}
                    >
                      {productReviews.length} Reviews
                    </span>
                  </h5>
                  <p style={{ color: '#6c757d', fontSize: '0.9rem' }}>
                    {product.product_description}
                  </p>

                  <b style={{ color: '#ffc107' }}>{formatPrice(product.product_cost)}</b>

                  {/* Reviews */}
                  <div className="mt-3">
                    {productReviews.length > 0 ? (
                      productReviews.map((review, index) => (
                        <div key={index} style={{ marginBottom: '10px' }}>
                          <div style={{ display: 'flex', alignItems: 'center' }}>
                            {renderStars(review.rating)}
                            <p className="ms-2" style={{ fontSize: '0.85rem', color: '#6c757d' }}>
                              {review.comment}
                            </p>
                          </div>
                        </div>
                      ))
                    ) : (
                      <p style={{ fontSize: '0.85rem', color: '#6c757d' }}>No reviews yet.</p>
                    )}
                  </div>

                  {/* Add Review Form */}
                  <div className="mt-3">
                    <textarea
                      value={newReview}
                      onChange={(e) => setNewReview(e.target.value)}
                      className="form-control"
                      placeholder="Write a review..."
                      rows="3"
                    />
                    <div className="mt-2">
                      {[1, 2, 3, 4, 5].map((star) => (
                        <span
                          key={star}
                          onClick={() => setRating(star)}
                          style={{ cursor: 'pointer', color: star <= rating ? '#92eb34' : '#e4e5e9' }}
                        >
                          ★
                        </span>
                      ))}
                    </div>
                    <button
                      className="btn mt-2 btn-outline-success"
                      onClick={() => submitReview(product.id)}
                    >
                      Submit Review
                    </button>
                    {reviewMessage && (
                      <p className="mt-2" style={{ color: '#fd7e14', fontSize: '0.85rem' }}>
                        {reviewMessage}
                      </p>
                    )}
                  </div>

                  <button
                    className="btn mt-3 text-white"
                    style={{ backgroundColor: '#28a745' }}
                    onClick={() => navigate('/mpesapayment', { state: { product } })}
                  >
                    <FaDollarSign className="me-2" /> Purchase Now
                  </button>

                  <button
                    className="btn mt-2 text-white"
                    style={{ backgroundColor: '#17a2b8' }}
                    onClick={() => handleAddToCart(product)}
                  >
                    <FaShoppingCart className="me-2" /> Add to Cart
                  </button>

                  {productMessage[product.id] && (
                    <p className="mt-2" style={{ color: '#eb3d34', fontSize: '0.85rem' }}>
                      {productMessage[product.id]}
                    </p>
                  )}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      <div className="text-center mt-4">
        <Link to="/faqs" className="btn btn-link" style={{ color: '#28a745' }}>
          Go to FAQ
        </Link>
      </div>

      {/* Floating Chatbot */}
      <div style={{
        position: 'fixed',
        bottom: '20px',
        right: '20px',
        zIndex: 1000,
      }}>
        <Chatbot />
      </div>
    </div>
  );
};

export default Getproducts;
