import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { CartContext } from './CartContext';
import { FaDollarSign, FaShoppingCart } from 'react-icons/fa';
import { Link } from 'react-router-dom';  // Import Link

const Getproducts = () => {
  const [loading, setLoading] = useState('');
  const [error, setError] = useState('');
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [productMessage, setProductMessage] = useState({});

  const { addToCart } = useContext(CartContext);
  const navigate = useNavigate();

  const get_products = async () => {
    setLoading('Please wait as we load the products...');
    try {
      const response = await axios.get('https://mercymucee.pythonanywhere.com/api/get_products_details');
      setProducts(response.data);
      setLoading('');
    } catch (error) {
      setLoading('');
      setError(error.message);
    }
  };

  useEffect(() => {
    get_products();
  }, []);

  useEffect(() => {
    const filtered = products.filter((product) =>
      product.product_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.product_description.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredProducts(filtered);
  }, [searchQuery, products]);

  const img_url = 'https://mercymucee.pythonanywhere.com/static/images/';

  // Color palette
  const primaryColor = '#28a745';      // Green
  const secondaryColor = '#f8f9fa';    // Light gray
  const accentColor = '#e63946';       // Soft red
  const infoColor = '#17a2b8';         // Sky blue
  const warningColor = '#fd7e14';      // Orange
  const textColor = '#212529';         // Dark text
  const mutedTextColor = '#6c757d';    // Muted

  const handleAddToCart = (product) => {
    setProductMessage((prev) => ({
      ...prev,
      [product.id]: 'Adding to cart...',
    }));

    setTimeout(() => {
      addToCart(product);
      setProductMessage((prev) => ({
        ...prev,
        [product.id]: 'Product added to cart successfully!',
      }));
      setTimeout(() => {
        setProductMessage((prev) => ({
          ...prev,
          [product.id]: '',
        }));
      }, 2000);
    }, 1000);
  };

  return (
    <div className="container py-4">
      <h1 className="mb-4 text-center" style={{ color: primaryColor }}>Available Products</h1>

      <input
        type="text"
        className="form-control shadow-sm mb-4 p-2"
        placeholder="Search products..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {loading && <p>{loading}</p>}
      {error && <p style={{ color: accentColor }}>{error}</p>}

      <div className="row">
        {filteredProducts.map((product) => (
          <div className="col-md-4 col-lg-3 mb-4" key={product.id}>
            <div
              className="card shadow-sm h-100"
              style={{
                backgroundColor: secondaryColor,
                border: '1px solid #ddd',
                borderRadius: '12px',
                overflow: 'hidden',
                position: 'relative',
              }}
            >
              <span
                className="badge position-absolute"
                style={{
                  top: '10px',
                  left: '10px',
                  backgroundColor: accentColor,
                  color: '#fff',
                  padding: '6px 10px',
                  fontSize: '0.75rem',
                  borderRadius: '12px',
                  zIndex: '2',
                }}
              >
                5% OFF
              </span>

              <img
                src={img_url + product.product_photo}
                alt={product.product_name}
                className="card-img-top"
                style={{
                  height: '220px',
                  objectFit: 'cover',
                  objectPosition: 'center',
                }}
              />

              <div className="card-body d-flex flex-column">
                <h5 style={{ color: textColor }}>{product.product_name}</h5>
                <p style={{ color: mutedTextColor, fontSize: '0.9rem' }}>
                  {product.product_description}
                </p>
                <b style={{ color: '#ffc107' }}>{product.product_cost}</b>

                <button
                  className="btn mt-3 text-white"
                  style={{ backgroundColor: primaryColor }}
                  onClick={() => navigate('/mpesapayment', { state: { product } })}
                >
                  <FaDollarSign className="me-2" /> Purchase Now
                </button>

                <button
                  className="btn mt-2 text-white"
                  style={{ backgroundColor: infoColor }}
                  onClick={() => handleAddToCart(product)}
                >
                  <FaShoppingCart className="me-2" /> Add to Cart
                </button>

                {productMessage[product.id] && (
                  <p className="mt-2" style={{ color: warningColor, fontSize: '0.85rem' }}>
                    {productMessage[product.id]}
                  </p>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="text-center mt-4">
        <Link to="/faqs" className="btn btn-link" style={{ color: primaryColor }}>Go to FAQ</Link>
      </div>
    </div>
  );
};

export default Getproducts;
